import React, { useState, useEffect } from 'react';
import axios from 'axios';

const History = () => {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = () => {
    axios.get('http://localhost:5000/history')
      .then(response => {
        setHistory(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the history!', error);
      });
  };

  const groupHistoryById = (history) => {
    return history.reduce((acc, item) => {
      if (!acc[item.id]) {
        acc[item.id] = [];
      }
      acc[item.id].push(item);
      return acc;
    }, {});
  };

  const groupedHistory = groupHistoryById(history);

  return (
    <div className="container mt-4">
      <style jsx>{`
        .card {
          border: none;
          border-radius: 8px;
          margin-bottom: 20px;
          box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
          font-family: 'Roboto', sans-serif;
          background: linear-gradient(135deg, #f3f4f6, #f9fafb);
        }
        .card-header {
          background-color: #007bff;
          color: #ffffff;
          border-radius: 8px 8px 0 0;
          padding: 10px;
        }
        .card-body {
          padding: 10px;
        }
        .card-title {
          margin-bottom: 0;
        }
        .list-group-item {
          background-color: #ffffff;
          border: 1px solid #dee2e6;
          border-radius: 8px;
          margin-bottom: 10px;
          padding: 10px;
        }
        .text-secondary {
          color: #6c757d !important;
        }
      `}</style>
      {Object.keys(groupedHistory).map(streamId => {
        const streamHistory = groupedHistory[streamId];
        const { companyName, name } = streamHistory[0];
        return (
          <div key={streamId} className="card">
            <div className="card-header">
              <h5 className="card-title">{`Stream ID: ${streamId}`}</h5>
              <p>{`Company Name: ${companyName}`}</p>
              <p>{`Name: ${name}`}</p>
            </div>
            <div className="card-body">
              {streamHistory.map((item, index) => (
                <div key={index} className="list-group-item">
                  <p><strong>Action:</strong> {item.action}</p>
                  <p><strong>Timestamp:</strong> {new Date(item.timestamp).toLocaleString()}</p>
                  <p><strong>Details:</strong> {item.details}</p>
                </div>
              ))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default History;
