import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const RadioProfile = () => {
  const { id } = useParams();
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [schedule, setSchedule] = useState([]);
  const [tracks, setTracks] = useState([]);
  const [newTrack, setNewTrack] = useState('');
  const [newTime, setNewTime] = useState('');

  useEffect(() => {
    fetchSchedule();
    fetchTracks();
  }, []);

  const fetchSchedule = () => {
    axios.get(`http://localhost:5000/radio/${id}/schedule`)
      .then(response => {
        setSchedule(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the schedule!', error);
      });
  };

  const fetchTracks = () => {
    axios.get(`http://localhost:5000/radio/${id}/tracks`)
      .then(response => {
        setTracks(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the tracks!', error);
      });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = () => {
    const formData = new FormData();
    formData.append('file', file);

    axios.post(`http://localhost:5000/radio/${id}/upload`, formData)
      .then(response => {
        setMessage('File uploaded successfully');
        fetchTracks(); // Refresh tracks after upload
      })
      .catch(error => {
        setMessage('File upload failed');
      });
  };

  const handleDeleteScheduleItem = (index) => {
    const updatedSchedule = schedule.filter((_, i) => i !== index);
    axios.post(`http://localhost:5000/radio/${id}/schedule`, updatedSchedule)
      .then(response => {
        setSchedule(updatedSchedule);
        setMessage('Schedule updated successfully');
      })
      .catch(error => {
        setMessage('Failed to update schedule');
        console.error('Error updating schedule:', error);
      });
  };

  const handleDeleteTrack = (track) => {
    const trackName = track.split('-').slice(1).join('-'); // Extract the track name after the first hyphen
    axios.delete(`http://localhost:5000/radio/${id}/tracks/${trackName}`)
      .then(response => {
        setMessage('Track deleted successfully');
        fetchTracks(); // Refresh tracks after deletion
      })
      .catch(error => {
        setMessage('Failed to delete track');
        console.error('Error deleting track:', error);
      });
  };

  const handleAddTrack = () => {
    const newSchedule = { track: newTrack, time: newTime };
    const updatedSchedule = [...schedule, newSchedule];
    setSchedule(updatedSchedule);
    axios.post(`http://localhost:5000/radio/${id}/schedule`, updatedSchedule)
      .then(response => {
        console.log('Schedule saved to server');
        fetchSchedule(); // Refresh schedule after adding new track
      })
      .catch(error => {
        console.error('Error saving schedule to server', error);
      });
    setNewTrack('');
    setNewTime('');
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Manage Radio Stream</h2>
      <div style={styles.inputGroup}>
        <input type="file" onChange={handleFileChange} style={styles.fileInput} />
        <button onClick={handleUpload} style={styles.uploadButton}>Upload</button>
        <p style={styles.message}>{message}</p>
      </div>

      <h2 style={styles.subHeading}>Schedule Tracks</h2>
      <div style={styles.inputGroup}>
        <input
          type="text"
          placeholder="Enter time (HH:MM:SS)"
          value={newTime}
          onChange={(e) => setNewTime(e.target.value)}
          style={styles.input}
        />
        <select
          value={newTrack}
          onChange={(e) => setNewTrack(e.target.value)}
          style={styles.select}
        >
          <option value="">Select Track</option>
          {tracks.map((track, index) => (
            <option key={index} value={track}>{track.split('-').slice(1).join('-')}</option>
          ))}
        </select>
        <button onClick={handleAddTrack} style={styles.addButton}>Add</button>
      </div>

      <h2 style={styles.subHeading}>Scheduled Tracks</h2>
      <ul style={styles.listGroup}>
        {schedule.map((item, index) => (
          <li key={index} style={styles.listItem}>
            {item.time} - {item.track}
            <button onClick={() => handleDeleteScheduleItem(index)} style={styles.deleteButton}>Delete</button>
          </li>
        ))}
      </ul>

      <h2 style={styles.subHeading}>Uploaded Tracks</h2>
      <ul style={styles.listGroup}>
        {tracks.map((track, index) => (
          <li key={index} style={styles.listItem}>
            {track.split('-').slice(1).join('-')}
            <button onClick={() => handleDeleteTrack(track)} style={styles.deleteButton}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

const styles = {
  container: {
    backgroundColor: '#f8f9fa',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 0 15px rgba(0, 0, 0, 0.1)',
    maxWidth: '800px',
    margin: '0 auto'
  },
  heading: {
    color: '#007bff',
    marginBottom: '20px',
    textAlign: 'center'
  },
  subHeading: {
    color: '#007bff',
    marginBottom: '20px'
  },
  inputGroup: {
    marginBottom: '15px'
  },
  input: {
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    borderRadius: '4px',
    border: '1px solid #ccc'
  },
  select: {
    width: '100%',
    padding: '10px',
    marginBottom: '10px',
    borderRadius: '4px',
    border: '1px solid #ccc'
  },
  fileInput: {
    display: 'block',
    marginBottom: '10px'
  },
  uploadButton: {
    display: 'block',
    width: '100%',
    padding: '10px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  addButton: {
    display: 'block',
    width: '100%',
    padding: '10px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  deleteButton: {
    backgroundColor: '#dc3545',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '5px 10px',
    cursor: 'pointer'
  },
  listGroup: {
    listStyleType: 'none',
    padding: '0'
  },
  listItem: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px',
    marginBottom: '10px',
    backgroundColor: '#fff',
    border: '1px solid #dee2e6',
    borderRadius: '4px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)'
  },
  message: {
    textAlign: 'center'
  }
};

export default RadioProfile;
