import React, { useState } from 'react';
import axios from 'axios';

const AddProfile = () => {
  const [name, setName] = useState('');
  const [url, setUrl] = useState('');
  const [companyName, setCompanyName] = useState('');
  const [subscriptionPlan, setSubscriptionPlan] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleAddProfile = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/radiostreams', {
        name,
        url,
        companyName,
        subscriptionPlan,
        email,
        username,
        password
      });
      setMessage('Profile added successfully');
    } catch (error) {
      setMessage('Error adding profile. Please try again.');
    }
  };

  return (
    <div className="add-profile">
      <h2>Add Radio Profile</h2>
      <form onSubmit={handleAddProfile}>
        <div>
          <label>Name</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div>
          <label>URL</label>
          <input type="text" value={url} onChange={(e) => setUrl(e.target.value)} required />
        </div>
        <div>
          <label>Company Name</label>
          <input type="text" value={companyName} onChange={(e) => setCompanyName(e.target.value)} required />
        </div>
        <div>
          <label>Subscription Plan</label>
          <input type="text" value={subscriptionPlan} onChange={(e) => setSubscriptionPlan(e.target.value)} required />
        </div>
        <div>
          <label>Email</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div>
          <label>Username</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
        </div>
        <div>
          <label>Password</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <button type="submit">Add Profile</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddProfile;
