import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Footer from './Footer';
import Header from './Header';

const History = () => {
  const [history, setHistory] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = () => {
    axios.get('http://localhost:5000/history')
      .then(response => {
        setHistory(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the history!', error);
      });
  };

  const handleBack = () => {
    navigate('/dashboard');
  };

  return (
    <div>
      <Header />
      <div className="container mt-4 history-container">
        <style jsx>{`
          .history-container {
            background-color: #f8f9fa;
            padding: 20px;
            border-radius: 8px;
            box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
            font-family: 'Roboto', sans-serif;
            background: linear-gradient(135deg, #f3f4f6, #f9fafb);
          }
          .history-container h2 {
            color: #007bff;
            margin-bottom: 20px;
            font-weight: 700;
            text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
          }
          .card {
            border: none;
            border-radius: 8px;
          }
          .history-card {
            background: linear-gradient(145deg, #e2ebf0, #f9f9f9);
            border-radius: 12px;
            box-shadow: 7px 7px 14px #c5c5c5, -7px -7px 14px #ffffff;
          }
          .list-group-item {
            background-color: #ffffff;
            border: 1px solid #dee2e6;
            border-radius: 12px;
            transition: transform 0.3s, box-shadow 0.3s;
            margin-bottom: 20px;
          }
          .list-group-item:hover {
            transform: scale(1.03);
            box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
          }
          .btn-back {
            background-color: #007bff;
            color: #ffffff;
            border: none;
            border-radius: 8px;
            padding: 10px 20px;
            cursor: pointer;
            transition: background-color 0.3s;
          }
          .btn-back:hover {
            background-color: #0056b3;
          }
        `}</style>
        <h2 className="text-primary">History</h2>
        <button className="btn-back" onClick={handleBack}>Back to Dashboard</button>
        <div className="list-group mt-4">
          {history.map((entry, index) => (
            <div key={index} className="list-group-item list-group-item-action mb-3 shadow-sm">
              <h4 className="text-secondary">Stream ID: {entry.id}</h4>
              <p><strong>Action:</strong> {entry.action}</p>
              <p><strong>Timestamp:</strong> {new Date(entry.timestamp).toLocaleString()}</p>
              <p><strong>Details:</strong> {entry.details}</p>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default History;
