const RadioPlayer = () => {
  const audioRef = useRef(null);
  const trackRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [volume, setVolume] = useState(1.0);
  const [tracks, setTracks] = useState([]);
  const [schedule, setSchedule] = useState([]);
  const [newTrack, setNewTrack] = useState('');
  const [newTime, setNewTime] = useState('');
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString('en-GB', { hour12: false }));
  const [isLoading, setIsLoading] = useState(false);
  const [isOnline, setIsOnline] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:5000/tracks')
      .then(response => {
        setTracks(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the tracks!', error);
      });

    axios.get('http://localhost:5000/schedule')
      .then(response => {
        setSchedule(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the schedule!', error);
      });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date().toLocaleTimeString('en-GB', { hour12: false });
      setCurrentTime(now);
      const scheduledTrack = schedule.find(track => track.time === now);
      if (scheduledTrack) {
        playScheduledTrack(scheduledTrack.track);
      }
    }, 1000);
    return () => clearInterval(interval);
  }, [schedule]);

  const handleLiveButtonClick = () => {
    setIsLoading(true);
    setIsOnline(true);
    audioRef.current.play()
      .then(() => setIsLoading(false))
      .catch(() => {
        setIsLoading(false);
        setIsOnline(false);
      });
  };

  const handleMuteButtonClick = () => {
    audioRef.current.muted = !audioRef.current.muted;
    setIsMuted(!isMuted);
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    audioRef.current.volume = newVolume;
    setVolume(newVolume);
  };

  const playScheduledTrack = (track) => {
    console.log('Attempting to play track:', track);
    fadeOut(audioRef.current, () => {
      trackRef.current.src = `http://localhost:5000/upload/${track}`;
      console.log('Track source set to:', trackRef.current.src);
      trackRef.current.load();
      trackRef.current.oncanplaythrough = () => {
        trackRef.current.play().catch(error => {
          console.error('Error playing track:', error);
        });
      };
      trackRef.current.onended = () => {
        fadeIn(audioRef.current);
        audioRef.current.play();
      };
    });
  };

  const fadeOut = (audio, callback) => {
    let volume = 1.0;
    const fadeAudio = setInterval(() => {
      if (volume > 0.1) {
        volume -= 0.1;
        audio.volume = volume;
      } else {
        clearInterval(fadeAudio);
        audio.volume = 0;
        callback();
      }
    }, 200);
  };

  const fadeIn = (audio) => {
    let volume = 0;
    const fadeAudio = setInterval(() => {
      if (volume < 0.9) {
        volume += 0.1;
        audio.volume = volume;
      } else {
        clearInterval(fadeAudio);
        audio.volume = 1.0;
      }
    }, 200);
  };

  const handleAddTrack = () => {
    const newSchedule = { track: newTrack, time: newTime };
    const updatedSchedule = [...schedule, newSchedule];
    setSchedule(updatedSchedule);
    axios.post('http://localhost:5000/schedule', updatedSchedule)
      .then(response => {
        console.log('Schedule saved to server');
      })
      .catch(error => {
        console.error('Error saving schedule to server', error);
      });
    setNewTrack('');
    setNewTime('');
  };

  return (
    <BackgroundContainer>
      <Logo src={`${process.env.PUBLIC_URL}/images/Hirdaramanilogo.png`} alt="Logo" />
      <br></br>     <br></br>     <br></br>     <br></br>
      <Container>
        <Sidebar>
          <GlobalStyle />
          <PlayerContainer>
            <Title>H BEAT - Sinhala</Title>
            <RadioImage src="/HBeat.jpg" alt="Radio" />
            <HiddenAudio ref={audioRef}>
              <source src="https://altair.streamerr.co/stream/8052" type="audio/mpeg" />
              Your browser does not support the audio element.
            </HiddenAudio>
            <Button onClick={handleLiveButtonClick}>Live</Button>
            <Button onClick={handleMuteButtonClick}>{isMuted ? 'Unmute' : 'Mute'}</Button>
            <VolumeControl
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={handleVolumeChange}
            />
            {isLoading && <Loading>Connecting...</Loading>}
            {!isOnline && <OfflineMessage>No connection.</OfflineMessage>}
          </PlayerContainer>
        </Sidebar>
        <ContentContainer>
          <Slideshow />
        </ContentContainer>
      </Container>
    </BackgroundContainer>
  );
};

export default RadioPlayer;
