import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const RadioStreamsList = () => {
  const [radioStreams, setRadioStreams] = useState([]);

  useEffect(() => {
    fetchRadioStreams();
  }, []);

  const fetchRadioStreams = () => {
    axios.get('http://localhost:5000/radiostreams')
      .then(response => {
        setRadioStreams(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the radio streams!', error);
      });
  };

  return (
    <div>
      <h2>Radio Streams</h2>
      <ul>
        {radioStreams.map(stream => (
          <li key={stream.id}>
            <p>{stream.name}</p>
            <Link to={`/radio/${stream.id}/profile`}>
              <button disabled={stream.alarmBlocked}>Go to Profile</button>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RadioStreamsList;
